    lasttask=task.New(self,function()
        do
            local _beg_al=0 local al=_beg_al local _end_al=200 local _d_al=(_end_al-_beg_al)/(30-1)
            local _beg_y=100 local y=_beg_y local _end_y=150 local _d_y=(_end_y-_beg_y)/(30-1)
